import React, {useState} from "react";

const UserData = ({users}) => {
    const [expandedGroups, setExpandedGroups] = useState({});

    const toggleGroup = (assetClass) => {
        setExpandedGroups((prevExpandedGroups) => ({
            ...prevExpandedGroups,
            [assetClass]: !prevExpandedGroups[assetClass]
        }));
    };

    return (
        <>
            {users.map((user, index) => {
                const { asset_class: assetClass } = user;
                const isGroupExpanded = expandedGroups[assetClass];
                const colspan = 6; // Number of columns in the table
                return (
                    <React.Fragment key={index}>
                        <tr onClick={() => toggleGroup(assetClass)}>
                            <td colSpan={colspan} style={{ cursor: 'pointer' }}>
                                {assetClass}
                                {isGroupExpanded ? "-" : "+"}
                            </td>
                        </tr>
                        {isGroupExpanded && (
                            <tr>
                                <td colSpan={colspan}>
                                    {/* Render individual holdings here */}
                                    <div>Name: {user.name}</div>
                                    <div>Ticker: {user.ticker}</div>
                                    <div>Average Price: {user.avg_price}</div>
                                    <div>Market Price: {user.market_price}</div>
                                    <div>Market Value CCY: {user.market_value_ccy}</div>
                                    <div>Latest Change Percentage: {user.latest_chg_pct}</div>
                                </td>
                            </tr>
                        )}
                    </React.Fragment>
                );
            })}
        </>
    );
}
export default UserData;
